import React from "react";
import { Container, makeStyles, Typography, TextField, Fab } from "@material-ui/core";
import { useHistory } from "react-router-dom";
import { v1 as uuid } from "uuid";

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        height: "100%",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
    },
    button: {
        width: "80px !important",
        marginLeft: theme.spacing(2),
        color: "white",
    },
    textContainer: {
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
    },
    textFieldAndButtonContainer: {
        marginTop: theme.spacing(2),
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
    },
}));

interface HomeProps {
    username: string;
    setUsername: (username: string) => void;
}

export const Home: React.FC<HomeProps> = (props: HomeProps) => {
    const { username, setUsername } = props;
    const classes = useStyles();
    const history = useHistory();

    const createRoom = () => {
        const id = uuid();
        history.push(`/room/${id}`);
    };

    return (
        <Container maxWidth="md" className={classes.root}>
            <div className={classes.textContainer}>
                <div>
                    <Typography variant="h3" component="div" style={{ flexGrow: 1 }}>
                        Welcome to Uvid
                    </Typography>
                </div>
                <div>
                    <Typography variant="h6" component="div">
                        Please enter your name before joining
                    </Typography>
                </div>
            </div>
            <div className={classes.textFieldAndButtonContainer}>
                <TextField
                    label="Add your name"
                    placeholder="Add your name"
                    variant="outlined"
                    value={username}
                    onChange={(event: any) => setUsername(event.target.value)}
                />
                <Fab variant="extended" size="medium" color="secondary" className={classes.button} onClick={createRoom}>
                    Join
                </Fab>
            </div>
        </Container>
    );
};
