import React, { useState } from "react";
import { makeStyles, Typography, TextField, Fab, Dialog, DialogContent } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        height: "100%",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
    },
    button: {
        width: "80px !important",
        marginLeft: theme.spacing(2),
        color: "white",
    },
    textContainer: {
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
    },
    textFieldAndButtonContainer: {
        marginTop: theme.spacing(2),
        marginBottom: theme.spacing(2),
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
    },
}));

interface NameDialogProps {
    open: boolean;
    close: () => void;
    setUsername: (username: string) => void;
}

export const NameDialog: React.FC<NameDialogProps> = (props: NameDialogProps) => {
    const { open, close, setUsername } = props;
    const classes = useStyles();
    const [value, setValue] = useState<string>("");

    const handleChooseUsername = () => {
        setUsername(value);
        close();
    };

    return (
        <Dialog open={open} onClose={close} maxWidth="md" fullWidth={true}>
            <DialogContent>
                <div className={classes.textContainer}>
                    <div>
                        <Typography variant="h3" component="div" style={{ flexGrow: 1 }}>
                            Welcome to Uvid
                        </Typography>
                    </div>
                    <div>
                        <Typography variant="h6" component="div">
                            Please enter your name before joining
                        </Typography>
                    </div>
                </div>
                <div className={classes.textFieldAndButtonContainer}>
                    <TextField
                        label="Add your name"
                        placeholder="Add your name"
                        variant="outlined"
                        onChange={(event: any) => setValue(event.target.value)}
                        value={value}
                    />
                    <Fab
                        variant="extended"
                        size="medium"
                        color="secondary"
                        className={classes.button}
                        onClick={handleChooseUsername}
                    >
                        Join
                    </Fab>
                </div>
            </DialogContent>
        </Dialog>
    );
};
