import React from "react";
import { makeStyles, Typography, TextField, Dialog, DialogContent } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
    textContainer: {
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
        marginTop: theme.spacing(2),
        marginBottom: theme.spacing(2),
    },
    marginBottom: {
        marginBottom: theme.spacing(2),
    },
}));

interface ShareDialogProps {
    open: boolean;
    close: () => void;
}

export const ShareDialog: React.FC<ShareDialogProps> = (props: ShareDialogProps) => {
    const { open, close } = props;
    const classes = useStyles();

    const getShareableLink = (): string => {
        return window.location.href;
    };

    return (
        <Dialog open={open} onClose={close} maxWidth="md" fullWidth={true}>
            <DialogContent>
                <div className={classes.textContainer}>
                    <div className={classes.marginBottom}>
                        <Typography variant="h4" component="div" style={{ flexGrow: 1 }}>
                            Here's your link
                        </Typography>
                    </div>
                    <div className={classes.marginBottom}>
                        <TextField
                            disabled
                            id="outlined-disabled"
                            defaultValue={getShareableLink()}
                            variant="outlined"
                        />
                    </div>
                    <div>
                        <Typography variant="h6" component="div">
                            Thanks for sharing!
                        </Typography>
                    </div>
                </div>
            </DialogContent>
        </Dialog>
    );
};
